import { useState } from 'react'
import lang from '../json_lib/language.json'
function WelcomePage(){
    const [keyword, setKeyword] = useState('')
    const [keyword2, setKeyword2] = useState([])
    var search_recommend = function(e){
        if(e.key == 'Backspace'){
            if(keyword.length !=0){
                setKeyword(keyword.substring(0, keyword.length-1))
                setKeyword2(keyword.substring(0, keyword.length-1))
            }
        }
        else{
            setKeyword(keyword + e.key)
        }
        console.log(keyword)
    } 
    const stopEvent = (e) => {
          e.preventDefault();
      }
    
    return (
        <div>
            안녕안녕
            <form onSubmit={stopEvent}>
                <input onKeyDown={search_recommend} ></input>
            </form>
        </div>
    )
}
export default WelcomePage;
